import React, { Component } from "react";
import tokenLogo from '../token-logo.png'
import ethLogo from '../eth-logo.png'

class Main extends Component {
  constructor(props){
    super(props);
    this.state = {
      output: '0',
      input: '0'
    }
  }
  render() {
    return (
      <div id="content">
        <div className="card mb-4">
          <div className="card-body">
            <form className="mb-3" onSubmit={(event)=>{
              event.preventDefault()
              let etheramount;
              etheramount = this.input.value.toString()
              etheramount = window.web3.utils.toWei(etheramount, 'Ether')
            }}>
              <div>
                <label className="float-left">
                  <b>Input</b>
                </label>
                <span className="float-right text-muted">Balance: {window.web3.utils.fromWei(this.props.ethBalance, 'Ether')}</span>
              </div>
              <div className="input-group mb-3">
                <input
                  type="text"
                  className="form-control form-control-lg"
                  placeholder="0"
                  onChange={(event)=>{

                    const etherAmount = this.input.value.toString();
                    this.setState({
                      output: etherAmount * 100
                    })
                  }}
                  ref={(input)=>{
                     this.input = input
                  }}
                  required
                />
                <div className="input-group-append">
                  <div className="input-group-text">
                    <img src={ethLogo} height="32" alt="" />
                    &nbsp;&nbsp;&nbsp; ETH
                  </div>
                </div>
              </div>
              <div>
                <label className="float-left">
                  <b>Output</b>
                </label>
                <span className="float-right text-muted">Balance: {window.web3.utils.fromWei(this.props.tokenBalance, 'Ether')}</span>
              </div>
              <div className="input-group mb-2">
                <input
                  type="text"
                  className="form-control form-control-lg"
                  placeholder="0"
                  value={this.state.output}
                  disabled

                />
                <div className="input-group-append">
                  <div className="input-group-text">
                    <img src={tokenLogo} height="32" alt="" />
                    &nbsp; ETH
                  </div>
                </div>
              </div>
              <div className="mb-5">
                <span className="float-left text-muted">Exchange Rate</span>
                <span className="float-right text-muted">1 ETH = 100 DApp</span>
              </div>
              <button
                type="submit"
                className="btn btn-primary btn-block btn-lg"
              >
                SWAP!
              </button>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default Main;
